mapper.select:
select * from product_tbl
where pno>0
order by pno desc, createdate desc limit #{startPage},#{perPageNum}

select * from product_tbl
where seller = #{userID} 
order by pno desc

mapper.update:
update product_tbl set 
nowprice=#{value}, 
tendercnt=tendercnt+1,
buyer=#{buyer} 
where pno=#{pno}

update product_tbl set
i_price=#{i_price}
where pno=#{pno}

update product_tbl set
finished=#{finished},
paid=#{paid}
where pno=#{pno}
